                selectId: originalSelect.attr('id'),
                inputId: 'form_autocomplete_input-' + $.now(),
                suggestionsId: 'form_autocomplete_suggestions-' + $.now(),
                selectionId: 'form_autocomplete_selection-' + $.now(),
                downArrowId: 'form_autocomplete_downarrow-' + $.now()
